import React from 'react';
import PropTypes from 'prop-types';


const variants = {
    base: "text-base",
    colors: {
        default: "text-black",
        white: 'text-gray-50',
    }
}

const Text = ({ textAlign, size, fontStyle, color = "default", bgcolor , bold = false, classeName, ...props }) => {
    const classNames = [
        "text-base",
        variants.colors[color],
        classeName
    ];

    if (bold) classNames.push('font-bold')
    if (textAlign) classNames.push(`text-${textAlign}`)
    if (size) classNames.push(`text-${size}`)
    if (fontStyle) classNames.push(`font-${fontStyle}`)
    if (bgcolor) classNames.push

    return (
        <p {...props}  className={classNames.join(' ')}></p>
    );
}

Text.prototype = {
    bold: PropTypes.bool,
    textAlign: 'left' | 'center',
    size: "sm" | "md",
    color: "default" | "white" | "gray" | "primary" | "danger" | "amber",
    fontStyle: "light"
}

export default Text;
