import React from 'react';
import PropTypes from 'prop-types';
import userImage from '../../assets/images/userImage.jpg';

const variant = {
    small: "h-8 w-8",
    big: "w-32 h-32",
}

const Avatar = ({ src = null, size="small", ...props }) => {
    const variantSize = variant[size];
    return (
        <>
            {
                src ?
                    <img className={`${variantSize}  rounded-full`} src={src} alt="Rounded avatar"></img>
                    :
                    <div className="relative overflow-hidden bg-gray-100 rounded-full dark:bg-gray-600">
                        <svg className={` absolute  text-gray-400 -left-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg`}>
                            <path fill-rule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clip-rule="evenodd"></path>
                        </svg>
                    </div>
            }
        </>
    );
}

Avatar.propTypes = {
    name: PropTypes.string,
    src: PropTypes.string
}

export default Avatar;
