import React from 'react';
import PropTypes from 'prop-types';

const variants = {
    size: {
        primary: {
            text: "text-white",
            color: "bg-green-900 hover:bg-green-700 focus:ring-4 focus:ring-green-500 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800",
        },
        light: {
            text: "text-gray-900",
            color: "bg-white hover:bg-gray-100 border border-gray-300 focus:ring-4 focus:ring-gray-300 dark:bg-gray-600 dark:hover:bg-gray-700 dark:focus:ring-gray-800"
        },
        amber: {
            text: "text-white",
            color: "bg-amber-400 hover:bg-amber-300 focus:ring-4 focus:ring-amber-200 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800",
        }
    }
}

const Avatar = ({ src = null, size, ...props }) => {
    return (
        <>
            {
                src ?
                    <img class="rounded-full" height={12} width={32} src={src} alt="Rounded avatar"></img>
                    :
                    <div class="relative w-10 h-10 overflow-hidden bg-gray-100 rounded-full dark:bg-gray-600">
                        <svg class="absolute w-12 h-12 text-gray-400 -left-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                            <path fill-rule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clip-rule="evenodd"></path>
                        </svg>
                    </div>
            }
        </>

    );
}


Avatar.propTypes = {
    name: PropTypes.string,
    src: PropTypes.string
}

export default Avatar;
