import React from 'react';
import PropTypes from 'prop-types';


const variants = {
    base: "text-base",
    colors: {
        default: "text-black",
        white: 'text-gray-50',
        gray: "text-gray-500 dark:text-gray-400",
        primary: "text-green-900 dark:text-green-900",
        danger: "text-red-600",
        amber: "text-amber-400"
    }
}

const Text = ({ textAlign, size, fontStyle, color = "default", bold = false, tw, ...props }) => {
    const classNames = [
        "text-base",
        variants.colors[color],
        tw
    ];

    if (bold) classNames.push('font-bold')
    if (textAlign) classNames.push(`text-${textAlign}`)
    if (size) classNames.push(`text-${size}`)
    if (fontStyle) classNames.push(`font-${fontStyle}`)

    return (
        <p {...props}  tw={classNames.join(' ')}></p>
    );
}

Text.prototype = {
    bold: PropTypesboolean,
    textAlign: 'left' | 'center',
    size: "sm" | "md",
    color: "default" | "white" | "gray" | "primary" | "danger" | "amber",
    fontStyle: "light"
}

export default Text;
